mapper.select:
select gg_good
from cy_gallery_good
where g_num = #{g_num} and m_id = #{m_id}

mapper.update:
UPDATE cy_gallery_good
SET gg_good = 1
WHERE g_num = #{g_num} and m_id = #{m_id}

UPDATE cy_gallery_good
SET gg_good = 0
WHERE g_num = #{g_num} and m_id = #{m_id}

update cy_gallery_comment
set gc_text = #{gc_text}
where gc_num = #{g_num}
