mapper.select:
		SELECT m_id ,m_pw
		FROM cy_member
    	WHERE m_id = #{m_id}

		SELECT m_id
		FROM cy_member
    	WHERE m_name = #{m_name}
    	AND m_email = #{m_email}

    	SELECT m_pw
		FROM cy_member
    	WHERE m_id = #{m_id}
    	AND m_email = #{m_email}

SELECT m_id
FROM cy_member
WHERE m_id = #{m_id}
